import React, { useState, useEffect } from 'react';
import './About.css';

const About = () => {
  const [expandedSection, setExpandedSection] = useState(null);
  const [skillAnimations, setSkillAnimations] = useState({});

  const skills = [
    { name: 'Python', percentage: 95, category: 'Programming' },
    { name: 'Machine Learning', percentage: 90, category: 'AI/ML' },
    { name: 'Mathematical Modeling', percentage: 92, category: 'Mathematics' },
    { name: 'Quantitative Finance', percentage: 88, category: 'Finance' },
    { name: 'PyTorch/TensorFlow', percentage: 85, category: 'AI/ML' },
    { name: 'Data Analysis', percentage: 90, category: 'Programming' }
  ];

  const stats = [
    { value: '3.97', label: 'Georgia Tech CGPA', symbol: '≈' },
    { value: '75+', label: 'Research Projects', symbol: '∑' },
    { value: '5', label: 'Publications', symbol: '∏' }
  ];

  const toggleSection = (section) => {
    setExpandedSection(expandedSection === section ? null : section);
  };

  useEffect(() => {
    if (expandedSection === 'skills') {
      const timer = setTimeout(() => {
        const animations = {};
        skills.forEach((skill, index) => {
          setTimeout(() => {
            setSkillAnimations(prev => ({
              ...prev,
              [skill.name]: true
            }));
          }, index * 200);
        });
      }, 300);
      return () => clearTimeout(timer);
    }
  }, [expandedSection]);

  return (
    <section id="about" className="about">
      <div className="mathematical-background">
        <div className="equation-overlay">∫ f(x)dx = ∑ ai·xi</div>
      </div>

      <div className="container">
        <div className="section-header">
          <h2 className="section-title">
            <span className="math-accent">∂</span>About<span className="math-accent">∂</span>
          </h2>
          <p className="section-subtitle">Mathematical Profile & Expertise</p>
        </div>

        <div className="about-grid">
          {/* Academic Background */}
          <div className="collapsible-card">
            <button 
              className="card-header"
              onClick={() => toggleSection('education')}
            >
              <div className="header-content">
                <span className="card-icon">∇</span>
                <div className="header-text">
                  <h3>Academic Foundation</h3>
                  <p>Educational Mathematics</p>
                </div>
              </div>
              <span className={`expand-icon ${expandedSection === 'education' ? 'expanded' : ''}`}>+</span>
            </button>

            {expandedSection === 'education' && (
              <div className="card-content">
                <div className="education-item">
                  <h4>Stanford University</h4>
                  <p className="degree">MS in Computational Mathematical Engineering</p>
                  <p className="focus">Mathematical Computational Finance Track</p>
                </div>
                <div className="education-item">
                  <h4>Georgia Institute of Technology</h4>
                  <p className="degree">BS in Applied Mathematics</p>
                  <p className="focus">CGPA: 3.97/4.0 | Summa Cum Laude</p>
                </div>
              </div>
            )}
          </div>

          {/* Skills Matrix */}
          <div className="collapsible-card">
            <button 
              className="card-header"
              onClick={() => toggleSection('skills')}
            >
              <div className="header-content">
                <span className="card-icon">λ</span>
                <div className="header-text">
                  <h3>Technical Proficiency</h3>
                  <p>Computational Skills Matrix</p>
                </div>
              </div>
              <span className={`expand-icon ${expandedSection === 'skills' ? 'expanded' : ''}`}>+</span>
            </button>

            {expandedSection === 'skills' && (
              <div className="card-content">
                <div className="skills-matrix">
                  {skills.map((skill, index) => (
                    <div key={skill.name} className="skill-row">
                      <div className="skill-info">
                        <span className="skill-name">{skill.name}</span>
                        <span className="skill-category">{skill.category}</span>
                        <span className="skill-value">{skill.percentage}%</span>
                      </div>
                      <div className="skill-bar">
                        <div 
                          className="skill-fill"
                          style={{
                            width: skillAnimations[skill.name] ? `${skill.percentage}%` : '0%'
                          }}
                        ></div>
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            )}
          </div>

          {/* Research Philosophy */}
          <div className="collapsible-card">
            <button 
              className="card-header"
              onClick={() => toggleSection('philosophy')}
            >
              <div className="header-content">
                <span className="card-icon">∞</span>
                <div className="header-text">
                  <h3>Research Philosophy</h3>
                  <p>Mathematical Approach</p>
                </div>
              </div>
              <span className={`expand-icon ${expandedSection === 'philosophy' ? 'expanded' : ''}`}>+</span>
            </button>

            {expandedSection === 'philosophy' && (
              <div className="card-content">
                <div className="philosophy-content">
                  <blockquote className="philosophy-quote">
                    "Mathematics is the language in which God has written the universe. 
                    My work seeks to decode this language in the context of computational finance 
                    and machine learning."
                  </blockquote>
                  <p className="philosophy-text">
                    I approach problems through the lens of mathematical rigor, 
                    computational efficiency, and practical application. My research 
                    bridges theoretical foundations with real-world financial applications.
                  </p>
                </div>
              </div>
            )}
          </div>
        </div>

        {/* Statistics Grid */}
        <div className="stats-section">
          <h3 className="stats-title">Academic Metrics</h3>
          <div className="stats-grid">
            {stats.map((stat, index) => (
              <div key={index} className="stat-card">
                <div className="stat-symbol">{stat.symbol}</div>
                <div className="stat-value">{stat.value}</div>
                <div className="stat-label">{stat.label}</div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </section>
  );
};

export default About;
