import React from 'react';
import './Contact.css';

const Contact = () => {
  const contactMethods = [
    {
      icon: 'üìß',
      label: 'Email',
      value: 'sukhani@stanford.edu',
      link: 'mailto:sukhani@stanford.edu',
      description: 'Primary Contact'
    },
    {
      icon: 'üíº',
      label: 'LinkedIn',
      value: 'siddhantukhani',
      link: 'https://www.linkedin.com/in/siddhantukhani/',
      description: 'Professional Profile'
    },
    {
      icon: 'üì±',
      label: 'Phone',
      value: '+1 (470) 827-4767',
      link: 'tel:+14708274767',
      description: 'Direct Contact'
    },
    {
      icon: 'üìç',
      label: 'Location',
      value: 'Stanford, CA',
      link: 'https://maps.google.com/?q=Stanford,CA',
      description: 'Bay Area'
    }
  ];

  return (
    <section id="contact" className="contact">
      <div className="container">
        <div className="contact-header">
          <h2 className="contact-title">Get In Touch</h2>
          <p className="contact-subtitle">Let's discuss research opportunities and collaborations</p>
        </div>

        <div className="contact-content">
          <div className="contact-text">
            <h3>Research Interests</h3>
            <p>
              I'm passionate about computational mathematics, quantitative finance, and machine learning. 
              Currently pursuing my MS in Computational Mathematical Engineering at Stanford University.
            </p>
            <div className="interests-list">
              <span className="interest-tag">Mathematical Finance</span>
              <span className="interest-tag">Machine Learning</span>
              <span className="interest-tag">Quantitative Analysis</span>
              <span className="interest-tag">Computational Methods</span>
            </div>
          </div>

          <div className="contact-methods">
            {contactMethods.map((method, index) => (
              <a 
                key={index} 
                href={method.link} 
                className="contact-method"
                target={method.label === 'LinkedIn' ? '_blank' : '_self'}
                rel={method.label === 'LinkedIn' ? 'noopener noreferrer' : ''}
              >
                <div className="method-icon">{method.icon}</div>
                <div className="method-details">
                  <h4 className="method-label">{method.label}</h4>
                  <p className="method-value">{method.value}</p>
                  <span className="method-description">{method.description}</span>
                </div>
              </a>
            ))}
          </div>
        </div>
      </div>
    </section>
  );
};

export default Contact;
