import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './Hero.css';
import MathGame from './MathGame';

const Hero = () => {
  const [currentEquation, setCurrentEquation] = useState(null);
  const [isGameOpen, setIsGameOpen] = useState(false);

  const equations = [
    // Numerical Analysis & Linear Algebra
    { formula: 'x̂ = F<sub>N</sub>x, (F<sub>N</sub>)<sub>kℓ</sub> = e<sup>-2πikℓ/N</sup>', description: 'Discrete Fourier Transform' },
    { formula: 'A = QR, Q<sup>T</sup>Q = I, R upper-triangular', description: 'QR Decomposition' },
    { formula: 'A = UΣV<sup>T</sup>, Σ = diag(σ<sub>1</sub>, σ<sub>2</sub>, ...)', description: 'Singular Value Decomposition' },
    { formula: 'β̂ = (X<sup>T</sup>X + λI)<sup>-1</sup>X<sup>T</sup>y', description: 'Ridge Regression Solution' },

    // Optimization & Machine Learning
    { formula: 'θ<sub>t+1</sub> = θ<sub>t</sub> - η∇<sub>θ</sub>L(θ<sub>t</sub>)', description: 'Gradient Descent Update Rule' },
    { formula: 'L = -Σ<sub>k</sub>y<sub>k</sub>log(softmax<sub>k</sub>(z))', description: 'Cross-Entropy Loss Function' },
    { formula: 'Attn(Q,K,V) = softmax(QK<sup>T</sup>/√d)V', description: 'Transformer Attention Mechanism' },
    { formula: 'min<sub>β</sub> ½||y - Xβ||² + λ||β||<sub>1</sub>', description: 'LASSO Regression Objective' },

    // Information Theory & Statistics
    { formula: 'H(X) = -Σ<sub>x</sub>p(x)log p(x)', description: 'Shannon Entropy' },
    { formula: 'D<sub>KL</sub>(P||Q) = Σ<sub>x</sub>p(x)log[p(x)/q(x)]', description: 'Kullback-Leibler Divergence' },
    { formula: 'I(X;Y) = Σ<sub>x,y</sub>p(x,y)log[p(x,y)/(p(x)p(y))]', description: 'Mutual Information' },
    { formula: 'Var(θ̂) ≥ I(θ)<sup>-1</sup>', description: 'Cramér-Rao Lower Bound' },

    // Stochastic Processes & Control
    { formula: 'df(t,S<sub>t</sub>) = ∂f/∂t dt + ∂f/∂S dS<sub>t</sub> + ½∂²f/∂S² σ²S<sub>t</sub>² dt', description: 'Itô\'s Lemma for Finance' },
    { formula: 'dS<sub>t</sub> = μS<sub>t</sub>dt + σS<sub>t</sub>dW<sub>t</sub>', description: 'Geometric Brownian Motion' },
    { formula: 'V<sup>π</sup>(s) = E<sub>π</sub>[r<sub>t+1</sub> + γV<sup>π</sup>(s<sub>t+1</sub>) | s<sub>t</sub> = s]', description: 'Bellman Equation for Value Function' },
    { formula: 'dX<sub>t</sub> = κ(θ - X<sub>t</sub>)dt + σdW<sub>t</sub>', description: 'Ornstein-Uhlenbeck Process' },

    // Finance & Econometrics
    { formula: '∂V/∂t + ½σ²S²∂²V/∂S² + rS∂V/∂S - rV = 0', description: 'Black-Scholes PDE' },
    { formula: 'C = S<sub>0</sub>Φ(d<sub>1</sub>) - Ke<sup>-rT</sup>Φ(d<sub>2</sub>)', description: 'Black-Scholes Call Option Price' },
    { formula: 'E[R<sub>i</sub>] = r<sub>f</sub> + β<sub>i</sub>(E[R<sub>m</sub>] - r<sub>f</sub>)', description: 'Capital Asset Pricing Model (CAPM)' },
    { formula: 'min<sub>w</sub> w<sup>T</sup>Σw s.t. w<sup>T</sup>μ = μ*, w<sup>T</sup>1 = 1', description: 'Portfolio Optimization Problem' },
    { formula: 'S = (E[R] - r<sub>f</sub>)/σ<sub>R</sub>', description: 'Sharpe Ratio' },
    { formula: 'σ<sub>t</sub>² = ω + αε<sub>t-1</sub>² + βσ<sub>t-1</sub>²', description: 'GARCH(1,1) Model' },

    // Algorithms & Complexity
    { formula: 'T(n) = aT(n/b) + Θ(n<sup>c</sup>) ⇒ T(n) = Θ(n<sup>c</sup>log n) if c = log<sub>b</sub>a', description: 'Master Theorem' },
    { formula: 'π<sup>T</sup> = π<sup>T</sup>(αP + (1-α)1v<sup>T</sup>)', description: 'PageRank Algorithm' },
    { formula: 'p ≈ (1 - e<sup>-kn/m</sup>)<sup>k</sup>', description: 'Bloom Filter False Positive Rate' },
    { formula: 'C = B log<sub>2</sub>(1 + S/N)', description: 'Shannon Channel Capacity' },

    // Signal Processing & Dynamics
    { formula: '(x * y)[n] = Σ<sub>k</sub>x[k]y[n-k]', description: 'Discrete Convolution' },
    { formula: 'S<sub>xx</sub>(f) = F{R<sub>xx</sub>(τ)}', description: 'Wiener-Khintchine Theorem' },
    { formula: 'ẋ = Ax + Bu, y = Cx + Du', description: 'Linear State-Space System' },
    { formula: 'x<sub>n+1</sub> = rx<sub>n</sub>(1 - x<sub>n</sub>)', description: 'Logistic Map (Chaos Theory)' },

    // PDEs & Physics
    { formula: '∂u/∂t = α∇²u', description: 'Heat Equation' },
    { formula: '∂²u/∂t² = c²∇²u', description: 'Wave Equation' },
    { formula: 'iℏ∂<sub>t</sub>ψ = -ℏ²/(2m)∇²ψ + Vψ', description: 'Schrödinger Equation' },
    { formula: '∇ × E = -∂<sub>t</sub>B', description: 'Faraday\'s Law (Maxwell Equations)' },

    // Cryptography & Security
    { formula: 'ed ≡ 1 (mod φ(N))', description: 'RSA Key Generation' },
    { formula: 'K = g<sup>ab</sup> mod p', description: 'Diffie-Hellman Key Exchange' },
    { formula: 'P(M|C) = P(M)', description: 'Perfect Secrecy Condition' },

    // Graph Theory & Combinatorics
    { formula: 'τ(G) = (1/n)λ<sub>2</sub>λ<sub>3</sub>...λ<sub>n</sub>', description: 'Matrix-Tree Theorem' },
    { formula: 'L = D - A with L1 = 0', description: 'Graph Laplacian Matrix' },
    { formula: 'p = (log n)/n for connectivity', description: 'Random Graph Connectivity Threshold' },

    // Machine Learning Gems
    { formula: 'ReLU(x) = max(0, x)', description: 'Rectified Linear Unit Activation' },
    { formula: 'σ(x) = 1/(1 + e<sup>-x</sup>)', description: 'Sigmoid Activation Function' },
    { formula: 'cos θ = (x·y)/(||x|| ||y||)', description: 'Cosine Similarity' },
    { formula: 'A<sup>+</sup> = VΣ<sup>+</sup>U<sup>T</sup>', description: 'Moore-Penrose Pseudoinverse' }
  ];

  const scrollToSection = (sectionId) => {
    const element = document.getElementById(sectionId);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
  };

  useEffect(() => {
    // Select a random equation on component mount
    const randomIndex = Math.floor(Math.random() * equations.length);
    setCurrentEquation(equations[randomIndex]);
  }, []);

  const openMathGame = () => {
    setIsGameOpen(true);
  };

  const closeMathGame = () => {
    setIsGameOpen(false);
  };

  if (!currentEquation) {
    return null; // Loading state
  }

  return (
    <section id="home" className="hero">
      {/* Mathematical Grid Background */}
      <div className="math-grid"></div>

      <div className="hero-content">
        <div className="hero-text">
          <h1 className="hero-title">
            <span className="name">Siddhant Sukhani</span>
            <span className="profession">∇ Computational Mathematical Engineer</span>
          </h1>

          {/* Dynamic Mathematical Formula */}
          <div className="math-formula">
            <span 
              className="formula-text clickable-equation" 
              dangerouslySetInnerHTML={{ __html: currentEquation.formula }}
              onClick={openMathGame}
              title="Click to play Mental Math Challenge!"
            ></span>
            <span className="formula-description">{currentEquation.description}</span>
          </div>

          <p className="hero-description">
            Stanford MS in Mathematical Computational Finance | Bridging Theory and Application
          </p>

          {/* Expandable Sections Navigation */}
          <div className="sections-grid">
            <Link to="/about" className="section-card">
              <div className="section-icon">∑</div>
              <div className="section-title">About</div>
              <div className="section-subtitle">Mathematical Background</div>
            </Link>

            <Link to="/research" className="section-card">
