import React, { useState, useEffect } from 'react';
import './MathGame.css';

const MathGame = ({ isOpen, onClose }) => {
  const [gameState, setGameState] = useState('ready'); // 'ready', 'playing', 'finished'
  const [timeLeft, setTimeLeft] = useState(60);
  const [score, setScore] = useState(0);
  const [currentProblem, setCurrentProblem] = useState(null);
  const [userAnswer, setUserAnswer] = useState('');
  const [totalQuestions, setTotalQuestions] = useState(0);

  const operations = ['+', '-', '×', '÷'];

  const generateProblem = () => {
    const operation = operations[Math.floor(Math.random() * operations.length)];
    let num1, num2, answer;

    // Generate 2-digit numbers (10-99)
    num1 = Math.floor(Math.random() * 90) + 10;
    num2 = Math.floor(Math.random() * 90) + 10;

    switch (operation) {
      case '+':
        answer = num1 + num2;
        break;
      case '-':
        // Ensure positive result
        if (num1 < num2) [num1, num2] = [num2, num1];
        answer = num1 - num2;
        break;
      case '×':
        // Use smaller numbers for multiplication to keep answers reasonable
        num1 = Math.floor(Math.random() * 20) + 10;
        num2 = Math.floor(Math.random() * 20) + 10;
        answer = num1 * num2;
        break;
      case '÷':
        // Ensure clean division
        answer = Math.floor(Math.random() * 20) + 5;
        num2 = Math.floor(Math.random() * 20) + 2;
        num1 = answer * num2;
        break;
      default:
        answer = 0;
    }

    return { num1, num2, operation, answer };
  };

  const startGame = () => {
    setGameState('playing');
    setTimeLeft(60);
    setScore(0);
    setTotalQuestions(0);
    setCurrentProblem(generateProblem());
    setUserAnswer('');
  };

  const checkAnswer = () => {
    if (parseInt(userAnswer) === currentProblem.answer) {
      setScore(score + 1);
    }
    setTotalQuestions(totalQuestions + 1);
    setCurrentProblem(generateProblem());
    setUserAnswer('');
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && userAnswer.trim() !== '') {
      checkAnswer();
    }
  };

  const resetGame = () => {
    setGameState('ready');
    setTimeLeft(60);
    setScore(0);
    setTotalQuestions(0);
    setCurrentProblem(null);
    setUserAnswer('');
  };

  useEffect(() => {
    let timer;
    if (gameState === 'playing' && timeLeft > 0) {
      timer = setTimeout(() => setTimeLeft(timeLeft - 1), 1000);
    } else if (timeLeft === 0 && gameState === 'playing') {
      setGameState('finished');
    }
    return () => clearTimeout(timer);
  }, [gameState, timeLeft]);

  if (!isOpen) return null;

  return (
    <div className="math-game-overlay">
      <div className="math-game-modal">
        <button className="close-button" onClick={onClose}>×</button>

        <div className="game-header">
          <h2>∇ Mental Math Challenge</h2>
          {gameState === 'playing' && (
            <div className="game-stats">
              <span className="timer">Time: {timeLeft}s</span>
              <span className="score">Score: {score}/{totalQuestions}</span>
            </div>
          )}
        </div>

        {gameState === 'ready' && (
          <div className="game-start">
            <div className="game-description">
              <p>Test your mental math skills!</p>
              <ul>
                <li>60 seconds to solve as many problems as possible</li>
                <li>Addition, subtraction, multiplication, and division</li>
                <li>Two-digit numbers for extra challenge</li>
                <li>Press Enter to submit your answer</li>
              </ul>
            </div>
            <button className="start-button" onClick={startGame}>
              Start Challenge
            </button>
          </div>
        )}

        {gameState === 'playing' && currentProblem && (
          <div className="game-playing">
            <div className="problem">
              <span className="problem-text">
                {currentProblem.num1} {currentProblem.operation} {currentProblem.num2} = ?
              </span>
            </div>
            <div className="answer-input">
              <input
                type="number"
                value={userAnswer}
                onChange={(e) => setUserAnswer(e.target.value)}
                onKeyPress={handleKeyPress}
                placeholder="Your answer"
                autoFocus
              />
              <button 
                onClick={checkAnswer} 
                disabled={userAnswer.trim() === ''}
                className="submit-button"
              >
                Submit
              </button>
            </div>
            <div className="progress-bar">
              <div 
                className="progress-fill" 
                style={{ width: `${((60 - timeLeft) / 60) * 100}%` }}
              ></div>
            </div>
          </div>
        )}

        {gameState === 'finished' && (
          <div className="game-finished">
            <div className="final-score">
              <h3>∞ Challenge Complete</h3>
              <div className="score-display">
                <span className="big-score">{score}</span>
                <span className="score-label">correct out of {totalQuestions}</span>
              </div>
              <div className="accuracy">
                Accuracy: {totalQuestions > 0 ? Math.round((score / totalQuestions) * 100) : 0}%
              </div>
              <div className="performance-message">
                {score >= totalQuestions * 0.9 ? "Outstanding Performance" :
                 score >= totalQuestions * 0.7 ? "Excellent Work" :
                 score >= totalQuestions * 0.5 ? "Good Effort" :
                 "Keep Practicing"}
              </div>
            </div>
            <div className="game-actions">
              <button className="play-again-button" onClick={startGame}>
                Play Again
              </button>
              <button className="close-game-button" onClick={onClose}>
                Close
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default MathGame;
