import React, { useEffect, useState } from 'react';

const QuoteComponent = () => {
  const [currentQuote, setCurrentQuote] = useState(null);

  const quotes = [
    // Philosophical quotes
    { text: "The unexamined life is not worth living.", author: "Socrates" },
    { text: "We are what we repeatedly do. Excellence, then, is not an act, but a habit.", author: "Aristotle" },
    { text: "The only true wisdom is in knowing you know nothing.", author: "Socrates" },
    { text: "What does not kill me, makes me stronger.", author: "Friedrich Nietzsche" },
    { text: "He who has a why to live can bear almost any how.", author: "Friedrich Nietzsche" },
    { text: "In the midst of winter, I found there was, within me, an invincible summer.", author: "Albert Camus" },
    { text: "The cave you fear to enter holds the treasure you seek.", author: "Joseph Campbell" },
    { text: "Man is condemned to be free; because once thrown into the world, he is responsible for everything he does.", author: "Jean-Paul Sartre" },
    { text: "The real question of life after death isn't whether or not it exists, but even if it does what problem this really solves.", author: "Ludwig Wittgenstein" },

    // Custom quotes reflecting Siddhant's perspective
    { text: "Every equation tells a story; every algorithm solves a human problem.", author: "Siddhant Sukhani" },
    { text: "In the intersection of mathematics and markets, lies the poetry of prediction.", author: "Siddhant Sukhani" },
    { text: "Data without intuition is noise; intuition without data is just a guess.", author: "Siddhant Sukhani" },
    { text: "The beauty of computational finance isn't in the complexity, but in making the complex elegantly simple.", author: "Siddhant Sukhani" },
    { text: "Mathematics is the language in which the universe writes its secrets; machine learning helps us read them.", author: "Siddhant Sukhani" },
    { text: "Excellence isn't perfection—it's the relentless pursuit of understanding deeper truths.", author: "Siddhant Sukhani" },
    { text: "Every model is wrong, but some are useful. The art is knowing which is which.", author: "Siddhant Sukhani" },
    { text: "Research isn't about finding answers; it's about asking questions that matter.", author: "Siddhant Sukhani" }
  ];

  useEffect(() => {
    // Add CSS animations to document head
    const style = document.createElement('style');
    style.textContent = `
      @keyframes fadeInQuote {
        0% {
          opacity: 0;
          transform: translateY(-20px) translateX(20px);
        }
        100% {
          opacity: 1;
          transform: translateY(0) translateX(0);
        }
      }
    `;
    document.head.appendChild(style);

    // Select a random quote on component mount
    const randomIndex = Math.floor(Math.random() * quotes.length);
    setCurrentQuote(quotes[randomIndex]);

    return () => {
      // Clean up the style element
      if (style.parentNode) {
        style.parentNode.removeChild(style);
      }
    };
  }, []);

  if (!currentQuote) return null;

  return (
    <div style={{
      position: 'fixed',
      top: '30px',
      right: '30px',
      maxWidth: '400px',
      background: 'rgba(255, 255, 255, 0.03)',
      backdropFilter: 'blur(20px)',
      border: '1px solid rgba(255, 255, 255, 0.1)',
      borderRadius: '16px',
      padding: '24px',
      zIndex: 1000,
      boxShadow: '0 8px 32px rgba(0, 0, 0, 0.3)',
      animation: 'fadeInQuote 2s ease-out'
    }}>
      <div style={{
        fontSize: '16px',
        lineHeight: '1.6',
        color: '#e5e5e5',
        fontStyle: 'italic',
        marginBottom: '12px',
        position: 'relative'
      }}>
        <span style={{
          position: 'absolute',
          left: '-12px',
          top: '-8px',
          fontSize: '24px',
          color: 'rgba(120, 119, 198, 0.6)',
          fontFamily: 'serif'
        }}>"</span>
        {currentQuote.text}
        <span style={{
          position: 'absolute',
          right: '-8px',
          bottom: '-16px',
          fontSize: '24px',
          color: 'rgba(120, 119, 198, 0.6)',
          fontFamily: 'serif'
        }}>"</span>
      </div>
      <div style={{
        fontSize: '14px',
        color: 'rgba(120, 119, 198, 0.9)',
        fontWeight: '500',
        textAlign: 'right',
        position: 'relative'
      }}>
        — {currentQuote.author}
      </div>
    </div>
  );
};

export default QuoteComponent;
