  return (
    <button 
      className="theme-toggle"
      onClick={toggleTheme}
      aria-label={`Switch to ${isDarkMode ? 'light' : 'dark'} mode`}
    >
      <span className="theme-icon">
        {isDarkMode ? '‚òÄÔ∏è' : 'üåô'}
      </span>
    </button>
  );
};

export default ThemeToggle;
