import React, { useState } from 'react';
import './Contact.css';

const Contact = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    subject: '',
    message: ''
  });

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Create mailto link with form data
    const subject = encodeURIComponent(formData.subject || 'Contact from Portfolio Website');
    const body = encodeURIComponent(
      `Name: ${formData.name}\nEmail: ${formData.email}\n\nMessage:\n${formData.message}`
    );
    window.location.href = `mailto:sukhani@stanford.edu?subject=${subject}&body=${body}`;
  };

  const contactMethods = [
    {
      icon: '✉',
      title: 'Email',
      value: 'sukhani@stanford.edu',
      link: 'mailto:sukhani@stanford.edu'
    },
    {
      icon: '📱',
      title: 'Phone',
      value: '+1 470-827-4767',
      link: 'tel:+14708274767'
    },
    {
      icon: '📍',
      title: 'Location',
      value: 'Atlanta, GA',
      link: null
    },
    {
      icon: '🔗',
      title: 'LinkedIn',
      value: 'siddhantsukhani',
      link: 'https://www.linkedin.com/in/siddhantsukhani/'
    }
  ];

  const socialLinks = [
    {
      name: 'GitHub',
      icon: '∇',
      url: 'https://github.com/siddhant250803',
      label: 'Code Repository'
    },
    {
      name: 'LinkedIn',
      icon: '∫',
      url: 'https://www.linkedin.com/in/siddhantsukhani/',
      label: 'Professional Network'
    },
    {
      name: 'Google Scholar',
      icon: '∑',
      url: 'https://scholar.google.com/citations?user=VAwr1v8AAAAJ&hl=en',
      label: 'Research Publications'
    },
    {
      name: 'Twitter',
      icon: 'λ',
      url: 'https://x.com/SukhaniSid29825',
      label: 'Social Updates'
    }
  ];

  const collaborationAreas = [
    'Quantitative Finance Research',
    'Machine Learning Applications',
    'Mathematical Modeling',
    'Financial Technology Innovation',
    'Academic Research Collaboration',
    'Open Source Projects'
  ];

  return (
    <section id="contact" className="contact">
      <div className="mathematical-background"></div>
      <div className="equation-overlay">
        ∇ · (communication) = collaboration
      </div>

      <div className="contact-content">
        <div className="contact-header">
          <h2 className="contact-title">Let's Connect</h2>
          <p className="contact-subtitle">Collaboration & Communication</p>
        </div>

        <div className="contact-main">
          <div className="contact-info">
            <div className="contact-methods">
              {contactMethods.map((method, index) => (
                <div key={index} className="method">
                  <div className="method-icon">{method.icon}</div>
                  <div className="method-details">
                    <h3>{method.title}</h3>
                    {method.link ? (
                      <a href={method.link} target={method.title === 'LinkedIn' ? '_blank' : '_self'} rel="noopener noreferrer">
                        {method.value}
                      </a>
                    ) : (
                      <span>{method.value}</span>
                    )}
                  </div>
                </div>
              ))}
            </div>

            <div className="collaboration-areas">
              <h3 className="areas-title">Areas of Interest</h3>
              <ul className="areas-list">
                {collaborationAreas.map((area, index) => (
                  <li key={index}>{area}</li>
                ))}
              </ul>
            </div>

            <div className="social-section">
              <h3 className="areas-title">Connect Online</h3>
              <div className="social-links">
                {socialLinks.map((link, index) => (
                  <a 
                    key={index}
                    href={link.url}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="social-link"
                  >
                    <span className="social-icon">{link.icon}</span>
                    <span className="social-label">{link.label}</span>
                  </a>
                ))}
              </div>
            </div>
          </div>

          <div className="contact-form">
            <form onSubmit={handleSubmit}>
              <div className="form-group">
                <label htmlFor="name" className="form-label">Name</label>
                <input
                  type="text"
                  id="name"
                  name="name"
                  value={formData.name}
                  onChange={handleChange}
                  className="form-input"
                  required
                />
              </div>

              <div className="form-group">
                <label htmlFor="email" className="form-label">Email</label>
                <input
                  type="email"
                  id="email"
                  name="email"
                  value={formData.email}
                  onChange={handleChange}
                  className="form-input"
                  required
                />
              </div>

              <div className="form-group">
                <label htmlFor="subject" className="form-label">Subject</label>
                <input
                  type="text"
                  id="subject"
                  name="subject"
                  value={formData.subject}
                  onChange={handleChange}
                  className="form-input"
                  required
                />
              </div>

              <div className="form-group">
                <label htmlFor="message" className="form-label">Message</label>
                <textarea
                  id="message"
                  name="message"
                  value={formData.message}
                  onChange={handleChange}
                  className="form-textarea"
                  rows="5"
                  required
                ></textarea>
              </div>

              <button type="submit" className="submit-btn">
                Send Message
              </button>
            </form>
          </div>
        </div>

        <div className="contact-footer">
          <div className="footer-equation">
            <span className="equation-text">∫ collaboration dx = innovation + C</span>
            <span className="equation-description">The Mathematics of Partnership</span>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Contact;
