import React from 'react';
import './Hero.css';

const Hero = () => {
  const scrollToSection = (sectionId) => {
    const element = document.getElementById(sectionId);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <section id="home" className="hero">
      {/* Mathematical Grid Background */}
      <div className="math-grid"></div>

      <div className="hero-content">
        <div className="hero-text">
          <h1 className="hero-title">
            <span className="name">Siddhant Sukhani</span>
            <span className="profession">âˆ‡ Computational Mathematical Engineer</span>
          </h1>

          {/* Mathematical Formula */}
          <div className="math-formula">
            <span className="formula-text">
              E[R] = R<sub>f</sub> + Î²(E[R<sub>m</sub>] - R<sub>f</sub>)
            </span>
            <span className="formula-description">CAPM: Where Finance Meets Mathematics</span>
          </div>

          <p className="hero-description">
            Stanford MS in Mathematical Computational Finance | Bridging Theory and Application
          </p>

          {/* Expandable Sections Navigation */}
          <div className="sections-grid">
            <button onClick={() => scrollToSection('about')} className="section-card">
              <div className="section-icon">âˆ‘</div>
              <div className="section-title">About</div>
              <div className="section-subtitle">Mathematical Background</div>
            </button>

            <button onClick={() => scrollToSection('projects')} className="section-card">
              <div className="section-icon">Î»</div>
              <div className="section-title">Research</div>
              <div className="section-subtitle">Quantitative Projects</div>
            </button>

            <button onClick={() => scrollToSection('contact')} className="section-card">
              <div className="section-icon">âˆž</div>
              <div className="section-title">Connect</div>
              <div className="section-subtitle">Collaboration</div>
            </button>
          </div>
        </div>
      </div>

      {/* Floating Mathematical Elements */}
      <div className="floating-elements">
        <div className="math-symbol symbol-1">Ï€</div>
        <div className="math-symbol symbol-2">âˆ‚</div>
        <div className="math-symbol symbol-3">âˆ«</div>
        <div className="math-symbol symbol-4">Î£</div>
        <div className="math-symbol symbol-5">âˆ‡</div>
      </div>
    </section>
  );
};

export default Hero;
