import React from 'react';
import './Hero.css';

const Hero = () => {
  const scrollToProjects = () => {
    const element = document.getElementById('projects');
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <section id="home" className="hero">
      <div className="hero-content">
        <div className="hero-text">
          <h1 className="hero-title">
            <span className="name">Siddhant Sukhani</span>
            <span className="profession">Computational Mathematician & ML Engineer</span>
          </h1>
          <p className="hero-description">
            Masters student at Stanford University specializing in Mathematical Computational Finance.
          </p>
          <div className="hero-buttons">
            <button onClick={scrollToProjects} className="cta-button primary">
              View Work
            </button>
            <button onClick={() => document.getElementById('contact').scrollIntoView({ behavior: 'smooth' })} className="cta-button secondary">
              Contact
            </button>
          </div>
        </div>
      </div>

      {/* Central Quote Section */}
      <div className="quote-section">
        <div className="quote-container">
          <blockquote className="quote-text">
            "Excellence isn't perfection—it's the relentless pursuit of understanding deeper truths."
          </blockquote>
          <cite className="quote-author">— Personal Philosophy</cite>
        </div>
      </div>

      <div className="scroll-indicator">
        <div className="scroll-arrow"></div>
      </div>
    </section>
  );
};

export default Hero;
