import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './Hero.css';
import MathGame from './MathGame';

const Hero = () => {
  const [showMathGame, setShowMathGame] = useState(false);
  const [currentEquationIndex, setCurrentEquationIndex] = useState(0);

  const equations = [
    {
      formula: "∫ f(x)dx = F(x) + C",
      description: "Fundamental Theorem of Calculus"
    },
    {
      formula: "E[R] = Rf + β(E[Rm] - Rf)",
      description: "Capital Asset Pricing Model"
    },
    {
      formula: "∂V/∂t + ½σ²S²∂²V/∂S² + rS∂V/∂S - rV = 0",
      description: "Black-Scholes Equation"
    },
    {
      formula: "P(A|B) = P(B|A)P(A) / P(B)",
      description: "Bayes' Theorem"
    }
  ];

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentEquationIndex((prev) => (prev + 1) % equations.length);
    }, 4000);

    return () => clearInterval(interval);
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  const scrollToSection = (sectionId) => {
    const element = document.getElementById(sectionId);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <section id="home" className="hero">
      <div className="hero-content">
        <div className="hero-text">
          <h1 className="hero-title">Siddhant Sukhani</h1>
          <h2 className="hero-subtitle">Computational Mathematical Engineer</h2>

          <div className="math-formula">
            <span className="formula-text">
              <span 
                className="clickable-equation"
                onClick={() => setShowMathGame(true)}
                role="button"
                tabIndex={0}
                onKeyPress={(e) => e.key === 'Enter' && setShowMathGame(true)}
              >
                {equations[currentEquationIndex].formula}
              </span>
            </span>
            <p className="formula-description">
              {equations[currentEquationIndex].description}
            </p>
          </div>

          <p className="hero-description">
            Masters student in Computational Mathematical Engineering at Stanford University, 
            specializing in Mathematical Computational Finance. Researching the intersection of 
            AI, mathematics, and finance through advanced quantitative methods and machine learning.
          </p>

          <div className="hero-buttons">
            <Link to="/about" className="btn-primary">
              About Me
            </Link>
            <Link to="/research" className="btn-secondary">
              View Research
            </Link>
            <button onClick={() => scrollToSection('contact')} className="btn-secondary">
              Get In Touch
            </button>
          </div>
        </div>
      </div>

      {showMathGame && (
        <MathGame onClose={() => setShowMathGame(false)} />
      )}
    </section>
  );
};

export default Hero;
